import React, { useState } from 'react';
import { motion } from 'framer-motion';
import { Mail, Phone, MapPin, Send, MessageCircle, CheckCircle, AlertCircle, Loader } from 'lucide-react';

const Contact = () => {
  const [formData, setFormData] = useState({
    name: '',
    email: '',
    company: '',
    message: ''
  });

  const [status, setStatus] = useState({
    submitting: false,
    submitted: false,
    error: false,
    errorMessage: ''
  });

  const handleChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value
    });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setStatus({ submitting: true, submitted: false, error: false, errorMessage: '' });

    try {
      // Simulación de envío (reemplaza con tu API real)
      await new Promise(resolve => setTimeout(resolve, 1500));

      setStatus({
        submitting: false,
        submitted: true,
        error: false,
        errorMessage: ''
      });

      setFormData({ name: '', email: '', company: '', message: '' });

    } catch (error) {
      setStatus({
        submitting: false,
        submitted: false,
        error: true,
        errorMessage: 'Hubo un error al enviar el mensaje. Por favor intenta nuevamente.'
      });
    }
  };

  const contactInfo = [
    {
      icon: Mail,
      title: "Email",
      value: "mendezalberto295@gmail.com",
      description: "Respuesta en 24 horas",
      color: "bg-blue-500"
    },
    {
      icon: Phone,
      title: "Teléfono",
      value: "+52 (33) 3225-0942",
      description: "Lun - Vie, 9:00 - 18:00",
      color: "bg-green-500"
    },
    {
      icon: MapPin,
      title: "Ubicación",
      value: "Guadalajara, Jalisco",
      description: "Trabajamos local y remotamente",
      color: "bg-purple-500"
    }
  ];

  return (
    <section id="contact" className="relative py-24 bg-white overflow-hidden">
      {/* Elementos decorativos */}
      <div className="absolute top-0 left-0 w-full h-32 bg-gradient-to-b from-gray-50 to-transparent"></div>
      <div className="absolute top-1/2 right-0 w-64 h-64 bg-lime-100 rounded-full mix-blend-multiply filter blur-3xl opacity-20"></div>

      <div className="container mx-auto px-6 max-w-6xl">
        <motion.div
          className="text-center mb-20"
          initial={{ opacity: 0, y: 50 }}
          whileInView={{ opacity: 1, y: 0 }}
          transition={{ duration: 0.8 }}
          viewport={{ once: true }}
        >
          <motion.span
            className="inline-flex items-center gap-2 px-4 py-2 bg-lime-50 text-lime-700 rounded-full text-sm font-medium mb-6"
            initial={{ opacity: 0, scale: 0.8 }}
            whileInView={{ opacity: 1, scale: 1 }}
            transition={{ delay: 0.2 }}
            viewport={{ once: true }}
          >
            <MessageCircle className="w-4 h-4" />
            Contacto directo
          </motion.span>

          <h2 className="text-4xl md:text-5xl font-bold text-gray-900 mb-6">
            Transformemos <span className="text-transparent bg-clip-text bg-gradient-to-r from-lime-600 to-green-500">ideas</span> en realidad
          </h2>

          <p className="text-lg text-gray-600 max-w-2xl mx-auto leading-relaxed">
            Completa el formulario o contáctanos directamente. Estamos listos para ayudarte a llevar tu proyecto al siguiente nivel.
          </p>
        </motion.div>

        <div className="grid grid-cols-1 lg:grid-cols-2 gap-16">
          {/* Contact Info */}
          <motion.div
            className="space-y-8"
            initial={{ opacity: 0, x: -50 }}
            whileInView={{ opacity: 1, x: 0 }}
            transition={{ duration: 0.8 }}
            viewport={{ once: true }}
          >
            {contactInfo.map((item, index) => (
              <motion.div 
                key={index}
                className="flex items-start gap-6 p-6 bg-white rounded-2xl shadow-sm border border-gray-100 hover:shadow-md transition-all duration-300"
                whileHover={{ y: -5 }}
                transition={{ delay: index * 0.1 }}
              >
                <div className={`flex-shrink-0 w-12 h-12 ${item.color} rounded-xl flex items-center justify-center text-white`}>
                  <item.icon className="w-6 h-6" />
                </div>
                <div>
                  <h3 className="text-lg font-semibold text-gray-900 mb-1">{item.title}</h3>
                  <p className="text-gray-900 font-medium mb-2">{item.value}</p>
                  <p className="text-gray-500 text-sm">{item.description}</p>
                </div>
              </motion.div>
            ))}

            <div className="mt-10 p-8 bg-gradient-to-br from-gray-900 to-gray-800 rounded-2xl text-white">
              <h3 className="text-xl font-bold mb-4">Horario de atención</h3>
              <ul className="space-y-3">
                <li className="flex justify-between">
                  <span className="text-gray-300">Lunes - Viernes</span>
                  <span className="font-medium">9:00 - 18:00</span>
                </li>
                <li className="flex justify-between">
                  <span className="text-gray-300">Sábados</span>
                  <span className="font-medium">10:00 - 14:00</span>
                </li>
                <li className="flex justify-between">
                  <span className="text-gray-300">Domingos</span>
                  <span className="font-medium">Cerrado</span>
                </li>
              </ul>
            </div>
          </motion.div>

          {/* Contact Form */}
          <motion.div
            className="relative"
            initial={{ opacity: 0, x: 50 }}
            whileInView={{ opacity: 1, x: 0 }}
            transition={{ duration: 0.8 }}
            viewport={{ once: true }}
          >
            <div className="absolute -top-4 -right-4 w-64 h-64 bg-lime-100 rounded-full mix-blend-multiply filter blur-3xl opacity-20"></div>

            <div className="relative bg-white p-8 md:p-10 rounded-2xl shadow-lg border border-gray-100">
              {status.error && (
                <motion.div
                  className="mb-6 p-4 bg-red-50 text-red-700 rounded-xl flex items-start gap-3"
                  initial={{ opacity: 0, y: -20 }}
                  animate={{ opacity: 1, y: 0 }}
                >
                  <AlertCircle className="w-5 h-5 mt-0.5 flex-shrink-0" />
                  <div>
                    <p className="font-medium">{status.errorMessage}</p>
                  </div>
                </motion.div>
              )}

              {status.submitted ? (
                <motion.div
                  className="text-center py-12"
                  initial={{ opacity: 0, scale: 0.8 }}
                  animate={{ opacity: 1, scale: 1 }}
                  transition={{ duration: 0.6 }}
                >
                  <motion.div
                    className="w-20 h-20 bg-gradient-to-r from-green-400 to-emerald-500 rounded-full flex items-center justify-center mx-auto mb-6"
                    initial={{ scale: 0 }}
                    animate={{ scale: 1 }}
                    transition={{ delay: 0.2, duration: 0.5, type: "spring" }}
                  >
                    <CheckCircle className="w-10 h-10 text-white" />
                  </motion.div>
                  <h3 className="text-2xl font-bold text-gray-800 mb-4">
                    ¡Mensaje enviado con éxito!
                  </h3>
                  <p className="text-gray-600 mb-6">
                    Gracias por contactarnos. Te responderemos en menos de 24 horas.
                  </p>
                  <motion.button
                    onClick={() => setStatus({...status, submitted: false})}
                    className="px-6 py-3 bg-gray-100 text-gray-800 rounded-xl font-medium hover:bg-gray-200 transition-colors"
                    whileHover={{ scale: 1.02 }}
                    whileTap={{ scale: 0.98 }}
                  >
                    Enviar otro mensaje
                  </motion.button>
                </motion.div>
              ) : (
                <form onSubmit={handleSubmit} className="space-y-6">
                  <div>
                    <label className="block text-sm font-medium text-gray-700 mb-2">
                      Nombre completo *
                    </label>
                    <input
                      type="text"
                      name="name"
                      value={formData.name}
                      onChange={handleChange}
                      required
                      className="w-full px-4 py-3 border border-gray-200 rounded-xl focus:outline-none focus:ring-2 focus:ring-lime-500 focus:border-transparent transition-all duration-300 placeholder-gray-400"
                      placeholder="Ej: Juan Pérez"
                    />
                  </div>

                  <div>
                    <label className="block text-sm font-medium text-gray-700 mb-2">
                      Email *
                    </label>
                    <input
                      type="email"
                      name="email"
                      value={formData.email}
                      onChange={handleChange}
                      required
                      className="w-full px-4 py-3 border border-gray-200 rounded-xl focus:outline-none focus:ring-2 focus:ring-lime-500 focus:border-transparent transition-all duration-300 placeholder-gray-400"
                      placeholder="Ej: tu@email.com"
                    />
                  </div>

                  <div>
                    <label className="block text-sm font-medium text-gray-700 mb-2">
                      Empresa (opcional)
                    </label>
                    <input
                      type="text"
                      name="company"
                      value={formData.company}
                      onChange={handleChange}
                      className="w-full px-4 py-3 border border-gray-200 rounded-xl focus:outline-none focus:ring-2 focus:ring-lime-500 focus:border-transparent transition-all duration-300 placeholder-gray-400"
                      placeholder="Nombre de tu empresa"
                    />
                  </div>

                  <div>
                    <label className="block text-sm font-medium text-gray-700 mb-2">
                      Mensaje *
                    </label>
                    <textarea
                      name="message"
                      value={formData.message}
                      onChange={handleChange}
                      required
                      rows="5"
                      className="w-full px-4 py-3 border border-gray-200 rounded-xl focus:outline-none focus:ring-2 focus:ring-lime-500 focus:border-transparent transition-all duration-300 resize-none placeholder-gray-400"
                      placeholder="Describe tu proyecto, necesidades o preguntas..."
                    ></textarea>
                  </div>

                  <motion.button
                    type="submit"
                    className="w-full px-6 py-4 bg-gradient-to-r from-lime-500 to-green-600 text-white font-semibold rounded-xl hover:shadow-lg transition-all duration-300 flex items-center justify-center gap-2 disabled:opacity-70"
                    whileHover={{ scale: 1.02 }}
                    whileTap={{ scale: 0.98 }}
                    disabled={status.submitting}
                  >
                    {status.submitting ? (
                      <>
                        <Loader className="w-5 h-5 animate-spin" />
                        Enviando...
                      </>
                    ) : (
                      <>
                        Enviar mensaje
                        <Send className="w-5 h-5" />
                      </>
                    )}
                  </motion.button>
                </form>
              )}
            </div>
          </motion.div>
        </div>
      </div>
    </section>
  );
};

export default Contact;
