import React from 'react';
import { motion } from 'framer-motion';
import { Users, Heart, Rocket, Star, MapPin, Calendar } from 'lucide-react';

const About = () => {
  const teamMembers = [
    {
      name: "Lindsey",
      role: "Co-fundadora & Estratega",
      description: "Visionaria del negocio con pasión por la innovación",
      color: "from-pink-400 to-rose-500"
    },
    {
      name: "Melissa",
      role: "Co-fundadora & Diseñadora",
      description: "Creativa que da vida a las ideas más ambiciosas",
      color: "from-purple-400 to-indigo-500"
    },
    {
      name: "Alberto",
      role: "Co-fundador & Desarrollador",
      description: "Arquitecto tecnológico que convierte sueños en código",
      color: "from-blue-400 to-cyan-500"
    }
  ];

  const milestones = [
    {
      year: "2019",
      title: "El Comienzo",
      description: "Tres mentes brillantes se unen para crear algo especial"
    },
    {
      year: "2021",
      title: "Primeros Éxitos",
      description: "Nuestros primeros clientes confían en nuestra visión"
    },
    {
      year: "2023",
      title: "Expansión",
      description: "Crecemos manteniendo nuestros valores familiares"
    },
    {
      year: "2025",
      title: "El Futuro",
      description: "Listos para conquistar nuevos horizontes tecnológicos"
    }
  ];

  return (
    <section id="about" className="py-20 bg-gradient-to-br from-gray-50 to-white">
      <div className="container mx-auto px-6">
        <motion.div
          className="text-center mb-16"
          initial={{ opacity: 0, y: 50 }}
          whileInView={{ opacity: 1, y: 0 }}
          transition={{ duration: 0.8 }}
          viewport={{ once: true }}
        >
          <motion.div
            className="inline-flex items-center gap-2 px-4 py-2 bg-blue-100 rounded-full text-blue-700 font-medium mb-6"
            initial={{ opacity: 0, scale: 0.8 }}
            whileInView={{ opacity: 1, scale: 1 }}
            transition={{ delay: 0.2 }}
            viewport={{ once: true }}
          >
            <Users className="w-4 h-4" />
            Nuestra Historia
          </motion.div>

          <h2 className="text-4xl md:text-5xl font-bold text-gray-800 mb-6">
            Una <span className="bg-gradient-to-r from-blue-600 to-purple-500 bg-clip-text text-transparent">familia</span> tecnológica
          </h2>

          <p className="text-xl text-gray-600 max-w-3xl mx-auto">
            LIMELAB nació de la unión de tres personas apasionadas por la tecnología y unidas por 
            lazos familiares. Lindsey + Melissa + LAB (de Alberto y laboratorio de ideas) = LIMELAB.
          </p>
        </motion.div>

        {/* Team Section */}
        <div className="mb-20">
          <motion.h3
            className="text-3xl font-bold text-center text-gray-800 mb-12"
            initial={{ opacity: 0, y: 30 }}
            whileInView={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.6 }}
            viewport={{ once: true }}
          >
            Conoce a nuestro equipo fundador
          </motion.h3>

          <div className="grid grid-cols-1 md:grid-cols-3 gap-8">
            {teamMembers.map((member, index) => (
              <motion.div
                key={index}
                className="text-center group"
                initial={{ opacity: 0, y: 50 }}
                whileInView={{ opacity: 1, y: 0 }}
                transition={{ delay: index * 0.2, duration: 0.6 }}
                viewport={{ once: true }}
              >
                <motion.div
                  className={`w-32 h-32 mx-auto mb-6 rounded-full bg-gradient-to-r ${member.color} flex items-center justify-center shadow-xl group-hover:shadow-2xl transition-all duration-300`}
                  whileHover={{ scale: 1.1, y: -5 }}
                >
                  <span className="text-4xl font-bold text-white">
                    {member.name.charAt(0)}
                  </span>
                </motion.div>

                <h4 className="text-xl font-bold text-gray-800 mb-2">{member.name}</h4>
                <p className="text-lime-600 font-semibold mb-3">{member.role}</p>
                <p className="text-gray-600">{member.description}</p>
              </motion.div>
            ))}
          </div>
        </div>

        {/* Timeline Section */}
        <motion.div
          className="mb-20"
          initial={{ opacity: 0, y: 50 }}
          whileInView={{ opacity: 1, y: 0 }}
          transition={{ duration: 0.8 }}
          viewport={{ once: true }}
        >
          <h3 className="text-3xl font-bold text-center text-gray-800 mb-12">
            Nuestro viaje a través del tiempo
          </h3>

          <div className="relative">
            <div className="absolute left-1/2 transform -translate-x-1/2 w-1 h-full bg-gradient-to-b from-blue-400 to-lime-400 rounded-full"></div>

            {milestones.map((milestone, index) => (
              <motion.div
                key={index}
                className={`flex items-center mb-12 ${index % 2 === 0 ? 'flex-row' : 'flex-row-reverse'}`}
                initial={{ opacity: 0, x: index % 2 === 0 ? -50 : 50 }}
                whileInView={{ opacity: 1, x: 0 }}
                transition={{ delay: index * 0.2, duration: 0.6 }}
                viewport={{ once: true }}
              >
                <div className={`w-1/2 ${index % 2 === 0 ? 'pr-8 text-right' : 'pl-8 text-left'}`}>
                  <div className="bg-white p-6 rounded-2xl shadow-lg border border-gray-200 hover:shadow-xl transition-shadow duration-300">
                    <div className="text-2xl font-bold text-lime-600 mb-2">{milestone.year}</div>
                    <h4 className="text-xl font-bold text-gray-800 mb-2">{milestone.title}</h4>
                    <p className="text-gray-600">{milestone.description}</p>
                  </div>
                </div>

                <div className="relative z-10">
                  <div className="w-6 h-6 bg-gradient-to-r from-blue-400 to-lime-400 rounded-full border-4 border-white shadow-lg"></div>
                </div>

                <div className="w-1/2"></div>
              </motion.div>
            ))}
          </div>
        </motion.div>

        {/* Mission & Vision */}
        <div className="grid grid-cols-1 lg:grid-cols-2 gap-8">
          <motion.div
            className="p-8 bg-gradient-to-br from-blue-50 to-indigo-50 rounded-3xl border border-blue-200/50"
            initial={{ opacity: 0, x: -50 }}
            whileInView={{ opacity: 1, x: 0 }}
            transition={{ duration: 0.8 }}
            viewport={{ once: true }}
          >
            <div className="flex items-center gap-3 mb-6">
              <div className="w-12 h-12 bg-gradient-to-r from-blue-500 to-indigo-600 rounded-xl flex items-center justify-center">
                <Rocket className="w-6 h-6 text-white" />
              </div>
              <h3 className="text-2xl font-bold text-gray-800">Nuestra Misión</h3>
            </div>
            <p className="text-gray-600 leading-relaxed">
              Desarrollar soluciones tecnológicas que mejoren la vida de las personas, 
              integrando innovación, empatía y el compromiso de una empresa familiar.
            </p>
          </motion.div>

          <motion.div
            className="p-8 bg-gradient-to-br from-lime-50 to-green-50 rounded-3xl border border-lime-200/50"
            initial={{ opacity: 0, x: 50 }}
            whileInView={{ opacity: 1, x: 0 }}
            transition={{ duration: 0.8 }}
            viewport={{ once: true }}
          >
            <div className="flex items-center gap-3 mb-6">
              <div className="w-12 h-12 bg-gradient-to-r from-lime-500 to-green-600 rounded-xl flex items-center justify-center">
                <Star className="w-6 h-6 text-white" />
              </div>
              <h3 className="text-2xl font-bold text-gray-800">Nuestra Visión</h3>
            </div>
            <p className="text-gray-600 leading-relaxed">
              Ser reconocidos en Latinoamérica como una empresa de tecnología confiable, 
              humana e innovadora, que pone a las personas en el centro de cada avance.
            </p>
          </motion.div>
        </div>
      </div>
    </section>
  );
};

export default About;
