import React from 'react';
import { motion } from 'framer-motion';

const Clients = () => {
  const clients = [
    {
      id: 1,
      name: "Empresa TechSolution",
      project: "Plataforma E-commerce",
      videoUrl: "https://ejemplo.com/video1",
      technologies: ["React", "Node.js", "MongoDB"],
      description: "Desarrollé una plataforma escalable con carrito de compras integrado y pasarela de pagos segura."
    },
    {
      id: 2,
      name: "Startup Verde",
      project: "App de Sustentabilidad",
      videoUrl: "https://ejemplo.com/video2",
      technologies: ["React Native", "Firebase", "Google Maps API"],
      description: "Aplicación móvil para tracking de huella de carbono con gamificación."
    },
    // Agrega más clientes según necesites
  ];

  return (
    <section id="clientes" className="py-20 bg-gray-50">
      <div className="container mx-auto px-4">
        <motion.h2 
          className="text-4xl font-bold text-center mb-16"
          initial={{ y: 50, opacity: 0 }}
          whileInView={{ y: 0, opacity: 1 }}
          transition={{ duration: 0.6 }}
        >
          Soluciones para mis Clientes
        </motion.h2>
        
        <div className="grid md:grid-cols-2 gap-12">
          {clients.map((client) => (
            <motion.div 
              key={client.id}
              className="bg-white rounded-xl shadow-lg overflow-hidden"
              initial={{ scale: 0.9, opacity: 0 }}
              whileInView={{ scale: 1, opacity: 1 }}
              transition={{ duration: 0.5 }}
              whileHover={{ y: -10 }}
            >
              <div className="aspect-w-16 aspect-h-9 bg-gray-200">
                {/* Aquí iría el componente de video */}
                <div className="flex items-center justify-center h-full">
                  <p className="text-gray-500">Video demostración de {client.project}</p>
                </div>
              </div>
              
              <div className="p-6">
                <h3 className="text-2xl font-semibold mb-2">{client.name}</h3>
                <h4 className="text-xl text-indigo-600 mb-4">{client.project}</h4>
                
                <p className="mb-4">{client.description}</p>
                
                <div className="mt-4">
                  <h5 className="font-medium mb-2">Tecnologías utilizadas:</h5>
                  <div className="flex flex-wrap gap-2">
                    {client.technologies.map((tech, index) => (
                      <span 
                        key={index}
                        className="px-3 py-1 bg-indigo-100 text-indigo-800 rounded-full text-sm"
                      >
                        {tech}
                      </span>
                    ))}
                  </div>
                </div>
              </div>
            </motion.div>
          ))}
        </div>
      </div>
    </section>
  );
};

export default Clients;
